mapper.insert:
update user 
	set user_nick = #{user_nick}
		, user_pw = #{user_pw}
		, user_pnum = #{user_pnum}
		, user_email = #{user_email}
		, user_email2 = #{user_email2}
		, user_addr = #{user_addr}
		, user_addr2 = #{user_addr2}
		, user_addr3 = #{user_addr3}
		, user_addr4 = #{user_addr4}
	where user_nick = #{user_nick}

mapper.select:
select user_pw 
	from user 
where user_nick = #{user_nick}
